mapper.select:
SELECT EMPLOYEETOTAL.employeeNo AS employeeNo, EMPLOYEETOTAL.employeeName AS employeeName, EMPLOYEETOTAL.department AS department, IFNULL(T_EMP_WORK2.pmId,'') AS pmId, IFNULL(T_EMP_WORK2.pmName,'') AS pmName, IFNULL(T_EMP_WORK2.caseName,'')AS caseName, IFNULL(EMPLOYEETOTAL.level,'') AS level, IFNULL(EMPLOYEETOTAL.professional,'') AS professional, IFNULL(STATUS.year,'')AS year, IFNULL(STATUS.useStatus01,'')AS useStatus01, IFNULL(STATUS.useStatus02,'') AS useStatus02, IFNULL(STATUS.useStatus03,'') AS useStatus03, IFNULL(STATUS.useStatus04,'') AS useStatus04, IFNULL(STATUS.useStatus05,'') AS useStatus05, IFNULL(STATUS.useStatus06,'') AS useStatus06, IFNULL(STATUS.useStatus07,'') AS useStatus07, IFNULL(STATUS.useStatus08,'') AS useStatus08, IFNULL(STATUS.useStatus09,'') AS useStatus09, IFNULL(STATUS.useStatus10,'') AS useStatus10, IFNULL(STATUS.useStatus11,'') AS useStatus11, IFNULL(STATUS.useStatus12,'') AS useStatus12 FROM (SELECT M_BP.VENDOR_ID AS employeeNo, M_BP.VENDOR_NAME AS employeeName, 'BP' AS department, '' AS level, '' AS professional FROM M_BP WHERE M_BP.DEL_FG='0'
AND M_BP.VENDOR_ID=#{employeeName} OR M_BP.VENDOR_NAME=#{employeeName}
UNION ALL SELECT M_EMPLOYEE.EMPLOYEE_NO AS employeeNo, M_EMPLOYEE.NAME AS employeeName, M_CODE.HANYOU_VALUE_NAME AS department, M_EMPLOYEE.LEVEL AS level, M_EMPLOYEE.PROFESSIONAL AS professional FROM M_EMPLOYEE LEFT JOIN M_EMP_DTL ON M_EMPLOYEE.EMPLOYEE_NO = M_EMP_DTL.EMPLOYEE_NO LEFT JOIN M_CODE ON (M_EMP_DTL.DEP_ROLE = M_CODE.HANYOU_VALUE AND M_CODE.HANYOU_CODE = 'DEP_ROLE' ) WHERE M_EMPLOYEE.DEL_FG=0
AND M_EMPLOYEE.LEVEL=#{level}
AND M_EMPLOYEE.PROFESSIONAL=#{department}
AND M_CODE.HANYOU_VALUE_NAME=#{department}
AND M_EMPLOYEE.EMPLOYEE_NO=#{employeeName} OR M_EMPLOYEE.NAME=#{employeeName} ) EMPLOYEETOTAL LEFT JOIN (SELECT T_EMP_WORK.EMPLOYEE_NO AS employeeNo, group_concat(M_EMPLOYEE.EMPLOYEE_NO) AS pmId, group_concat(M_EMPLOYEE.NAME) AS pmName, group_concat(T_EMP_WORK.CASE_NAME) AS caseName FROM T_EMP_WORK LEFT JOIN M_EMPLOYEE ON T_EMP_WORK.PM_EMPLOYEE_NO=M_EMPLOYEE.EMPLOYEE_NO WHERE T_EMP_WORK.YEAR=year(now()) AND T_EMP_WORK.USE_MONTH=month(now()) GROUP BY T_EMP_WORK.EMPLOYEE_NO) T_EMP_WORK2 ON EMPLOYEETOTAL.employeeNo=T_EMP_WORK2.employeeNo LEFT JOIN (select T_EMP_WORK.EMPLOYEE_NO as employeeNo, T_EMP_WORK.YEAR AS year, sum(CASE WHEN T_EMP_WORK.USE_MONTH ='01' THEN T_EMP_WORK.USE_STATUS else ''end) as useStatus01, sum(CASE WHEN T_EMP_WORK.USE_MONTH ='02' THEN T_EMP_WORK.USE_STATUS else ''end) as useStatus02, sum(CASE WHEN T_EMP_WORK.USE_MONTH ='03' THEN T_EMP_WORK.USE_STATUS else ''end) as useStatus03, sum(CASE WHEN T_EMP_WORK.USE_MONTH ='04' THEN T_EMP_WORK.USE_STATUS else ''end) as useStatus04, sum(CASE WHEN T_EMP_WORK.USE_MONTH ='05' THEN T_EMP_WORK.USE_STATUS else ''end) as useStatus05, sum(CASE WHEN T_EMP_WORK.USE_MONTH ='06' THEN T_EMP_WORK.USE_STATUS else ''end) as useStatus06, sum(CASE WHEN T_EMP_WORK.USE_MONTH ='07' THEN T_EMP_WORK.USE_STATUS else ''end) as useStatus07, sum(CASE WHEN T_EMP_WORK.USE_MONTH ='08' THEN T_EMP_WORK.USE_STATUS else ''end) as useStatus08, sum(CASE WHEN T_EMP_WORK.USE_MONTH ='09' THEN T_EMP_WORK.USE_STATUS else ''end) as useStatus09, sum(CASE WHEN T_EMP_WORK.USE_MONTH ='10' THEN T_EMP_WORK.USE_STATUS else ''end) as useStatus10, sum(CASE WHEN T_EMP_WORK.USE_MONTH ='11' THEN T_EMP_WORK.USE_STATUS else ''end) as useStatus11, sum(CASE WHEN T_EMP_WORK.USE_MONTH ='12' THEN T_EMP_WORK.USE_STATUS else ''end) as useStatus12 FROM T_EMP_WORK WHERE T_EMP_WORK.DEL_FG='0' GROUP BY T_EMP_WORK.EMPLOYEE_NO, T_EMP_WORK.YEAR ) STATUS ON EMPLOYEETOTAL.employeeNo=STATUS.employeeNo
