mapper.select:
SELECT T.* FROM ( SELECT VEW.employeeNo, VEW.employeeName, VEW.department, VEW.level, VEW.professional, '2020' as year, max(if (VEW.`month`=date_format(CURDATE(),'%m'),VEW.pmId,'')) pmId, max(if (VEW.`month`=date_format(CURDATE(),'%m'),VEW.pmName,'')) pmName, max(if (VEW.`month`=date_format(CURDATE(),'%m'),VEW.caseName,'')) caseName, max(if (VEW.month = '01',VEW.useStatus,'')) useStatus01, max(if (VEW.month = '02',VEW.useStatus,'')) useStatus02, max(if (VEW.month = '03',VEW.useStatus,'')) useStatus03, max(if (VEW.month = '04',VEW.useStatus,'')) useStatus04, max(if (VEW.month = '05',VEW.useStatus,'')) useStatus05, max(if (VEW.month = '06',VEW.useStatus,'')) useStatus06, max(if (VEW.month = '07',VEW.useStatus,'')) useStatus07, max(if (VEW.month = '08',VEW.useStatus,'')) useStatus08, max(if (VEW.month = '09',VEW.useStatus,'')) useStatus09, max(if (VEW.month = '10',VEW.useStatus,'')) useStatus10, max(if (VEW.month = '11',VEW.useStatus,'')) useStatus11, max(if (VEW.month = '12',VEW.useStatus,'')) useStatus12 FROM V_WORK_DATA AS VEW
GROUP BY VEW.employeeNo, VEW.employeeName, VEW.department, VEW.level, VEW.professional ORDER BY VEW.employeeNo ) T
AND (T.employeeNo like CONCAT('%',#{name},'%') or T.employeeName like CONCAT('%',#{name},'%') )
AND T.year = YEAR(CURDATE())
AND T.LEVEL in #{level}
AND T.department in #{department}
AND T.professional IN #{professional}
AND T.pmId = #{pmName}
AND ( EXISTS( SELECT TEW.EMPLOYEE_NO FROM T_EMP_WORK TEW WHERE TEW.EMPLOYEE_NO = T.employeeNo AND CONCAT(year,USE_MONTH) >= #{startDt} AND CONCAT(year,USE_MONTH) <= #{endDt} AND (USE_STATUS = 0 AND CASE_ID is null ) ) OR NOT EXISTS ( SELECT TEW.EMPLOYEE_NO FROM T_EMP_WORK TEW WHERE TEW.EMPLOYEE_NO = T.employeeNo AND CONCAT(year,USE_MONTH) >= #{startDt} AND CONCAT(year,USE_MONTH) <= #{endDt}))
